class Solution(object):
    def longestValidParenthesesNaive(self, s):
        """
        :type s: str
        :rtype: int
        """
        maxlen = 0

        for i in range(len(s)):
            cur = 0
            for k in range(i,len(s)):
                if s[k] == ')':
                    cur -= 1
                if s[k] == '(':
                    cur += 1
                if cur < 0:
                    break
                if cur == 0:
                    maxlen = max(maxlen, k - i + 1)

        return maxlen


    def longestValidParentheses(self, s):
        """
        :type s: str
        :rtype: int
        """
        maxlen = 0
        stack = []


        for i in range(len(s)):
            if s[i] == '(':
                stack.append(i)
            else:
                if len(stack) > 0 and s[stack[-1]] == '(':
                    stack.pop()
                else:
                    stack.append(i)


        a = len(s)
        print(stack)

        if len(stack) == 0:
            return a

        b = 0

        while len(stack) > 0:
            b = stack.pop()
            maxlen = max(maxlen, a-b-1)
            a = b
        maxlen = max(maxlen, a)

        return maxlen



